import React, { Component } from 'react';
import PropTypes from 'prop-types';

import "../css/BetCard.css";

class BetCard extends Component {
    constructor(props) {
        this.state = {
            colSize: "col-md-4"
        };
    }

    getFee(percent) {
        let res = 100 / percent;

        return Math.round(res * 100) / 100;
    }

    componentDidMount() {
        this.updateWindowDimensions();
        window.addEventListener('resize', this.updateWindowDimensions);
    }

    componentWillUnmount() {
        window.removeEventListener('resize', this.updateWindowDimensions);
    }


    updateWindowDimensions() {
        let nColSize = "col"

        let width = window.innerWidth;

        if (width >= 1500) {
            nColSize = "col-md-3";
        } else if (width > 800 && width < 1500) {
            nColSize = "col-md-4";
        } else {
            nColSize = "col-md-6";
        }

        this.setState({ colSize: nColSize });
    }

    render() {
        const txtLoading = "Loading...";
        let Name = txtLoading;
        let Place = txtLoading;
        let Team1 = txtLoading;
        let Team2 = txtLoading;
        let Prob1 = txtLoading;
        let Tie = txtLoading;
        let Prob2 = txtLoading;

        let bInfo = this.props.betInfo;
        let eInfo = this.props.eventInfo;

        if (eInfo != null && eInfo != undefined && eInfo.length > 0) {
            eInfo = eInfo[0];

            Name = eInfo.Name;
            Place = eInfo.Place;
            Team1 = eInfo.Team1;
            Team2 = eInfo.Team2;
            Prob1 = eInfo.Prob1;
            Tie = eInfo.Tie;
            Prob2 = eInfo.Prob2;
        }

        return (
            <div id={Name} className={this.state.colSize + " myBetCard"}>
                <div className="card myCard">
                    <img className="card-img-top" src="img/Basic_Event.png" alt="Card image cap" />
                    <div className="card-body">
                        <h5>{width}</h5>
                        <h4 className="card-title">{Name}</h4>
                        <h5 className="card-title">{Place}</h5>

                        <div className="btn-group myButtonGroup">
                            <span className="badge badge-primary myButtonGroup">{Team1}</span>
                            <span className="badge badge-secondary myButtonGroup">Tie</span>
                            <span className="badge badge-primary myButtonGroup">{Team2}</span>
                        </div>

                        <div className="btn-group myButtonGroup" role="group" aria-label="Basic example">
                            <button type="button" className="btn btn-primary myButtonOnGroup">{"" + this.getFee(Prob1)}</button>
                            <button type="button" className="btn btn-secondary myButtonOnGroup">{"" + this.getFee(Tie)}</button>
                            <button type="button" className="btn btn-primary myButtonOnGroup">{"" + this.getFee(Prob2)}</button>
                        </div>

                        <hr className="my-4" />
                        <h5 className="card-title">Bets...</h5>

                        <div className="btn-group myButtonGroup" role="group" aria-label="Basic example">
                            <button type="button" className="btn btn-warning myButtonOnGroup">{bInfo.Team1}</button>
                            <button type="button" className="btn btn-warning myButtonOnGroup">{bInfo.Tie}</button>
                            <button type="button" className="btn btn-warning myButtonOnGroup">{bInfo.Team2}</button>
                        </div>

                        <hr className="my-4" />

                        <button className="btn btn-outline-info myCardButton" type="submit">More info</button>
                    </div>
                </div>
            </div>
        );
    }
}

BetCard.propTypes = {
    betInfo: PropTypes.object.isRequired,
    eventInfo: PropTypes.array.isRequired
}

export default BetCard;
