import React, { Component } from 'react';
import { withHistory, Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { withTracker } from "meteor/react-meteor-data";

import BasicNav from "./BasicNav.jsx";

import { UserData } from "../../api/UserDataMethods";

import "../css/MainPage.css";

class MainPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            userData: null
        };
    }

    componentDidMount() {
        let currentUser = this.props.user;
        let userDataAvailable = (currentUser !== undefined);
        let loggedIn = (currentUser && userDataAvailable);

        if (loggedIn) {
            Meteor.call("UserData.getData", currentUser._id, (err, res) =>{
                console.log("UserData.getData response: ");
                console.log(err);
                console.log(res);

                this.setState({
                    userData: res[0]
                });
            });
        }
    }

    render() {
        let currentUser = this.props.user;
        let currentUserAvailable = (currentUser !== undefined);

        let currentUserData = this.state.userData;
        let userDataAvailable = (currentUserData !== undefined);

        let loggedIn = (currentUser && currentUserAvailable && currentUserData && userDataAvailable);

        //let currentUserData = this.props.userData;
        console.log(currentUserData);

        let numCoins = 0;

        if (loggedIn) {
            console.log("UserData: ");
            console.log(currentUserData);
        }

        return (
            <div>
                {loggedIn ? <BasicNav logout={this.props.logout} userName={currentUser.username} coins={numCoins} /> : "Error"}

                <div className="container-fluid">
                    <div className="row">
                        <nav className="col-md-2 d-none d-md-block bg-light sidebar">
                            <div className="sidebar-sticky">
                                <ul className="nav flex-column">
                                    <li className="nav-item">
                                        <a href="#" className="nav-link active">
                                            Football
                                        </a>
                                    </li>

                                    <li className="nav-item">
                                        <a href="#" className="nav-link">
                                            Basketball
                                        </a>
                                    </li>
                                </ul>
                            </div>
                        </nav>

                        <main role="main" className="col-md-9 ml-sm-auto col-lg-10 pt-3 px-4">

                            <h1 className="text-center">
                                {loggedIn ? 'Welcome ' + currentUser.username : 'No user/userData found'}
                            </h1>

                        </main>
                    </div>

                </div>
            </div>
        );
    }
}

MainPage.propTypes = {
    user: PropTypes.object.isRequired
}

export default withTracker(
    () => {
        Meteor.subscribe('UserData');

        return {
            userData: UserData.find({}).fetch()
        }
    }
)(MainPage);
