import React, { Component } from 'react';
import PropTypes from 'prop-types';

class AddBetModal extends Component {
    constructor(props) {
        super(props);

        this.state = {
            bet1: 0,
            bet2: 0,
            betT: 0
        }

        this.handleChange = this.handleChange.bind(this);
    }

    getFee(percent) {
        let res = 100 / percent;

        return Math.round(res * 100) / 100;
    }

    getEarnings(){
        let earnings = 0;

        let eInfo = this.props.eventInfo;

        earnings += this.getFee(eInfo.Prob1)*this.state.bet1;
        earnings += this.getFee(eInfo.Prob2)*this.state.bet2;
        earnings += this.getFee(eInfo.Tie)*this.state.betT;

        return earnings;
    }

    handleChange(event) {
        this.setState({ [event.target.id]: event.target.value });
    }

    render() {
        console.log(this.props.eventInfo);

        return (
            <div id={"Add" + this.props.eventInfo.Name + "BetModal"} className="modal fade" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Add bet</h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>

                        <div className="modal-body">
                            <div className="form-row">
                                <div className="input-group col-md-4">
                                    <input type="number" min="0" step="0.01" className="form-control" id="bet1" placeholder="" value={this.state.bet1} onChange={this.handleChange} />
                                </div>

                                <div className="input-group col-md-4">
                                    <input type="number" min="0" step="0.01" className="form-control" id="betT" placeholder="" value={this.state.betT} onChange={this.handleChange} />
                                </div>

                                <div className="input-group col-md-4">
                                    <input type="number" min="0" step="0.01" className="form-control" id="bet2" placeholder="" value={this.state.bet2} onChange={this.handleChange} />
                                </div>
                            </div>

                            <div className="form-group row">
                                <label htmlFor="staticEmail" class="col-sm-2 col-form-label">Earnings:</label>
                                <div class="col-sm-10">
                                    <input className="form-control" type="text" placeholder={this.getEarnings() + ""} readOnly />
                                </div>
                            </div>

                            <div className="modal-footer">
                                <button type="button" className="btn btn-secondary" data-dismiss="modal">Cancel</button>
                                <button type="button" className="btn btn-primary">Bet!</button>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

AddBetModal.propTypes = {
    eventInfo: PropTypes.object.isRequired
}

export default AddBetModal;
