import React, { Component } from 'react';
import PropTypes from 'prop-types';

import EventCard from "./EventCard.jsx";
import "../css/CategoryPage.css";

class CategoryPage extends Component {

    AddEvent() {
        if (this.props.userData.type == "ADMIN") {
            alert("Adding the event!");
            this.closeAddBoardModal;
        } else {
            alert("You do not have rights to do that!");
        }
    }

    listTypeOptions() {

    }

    loadEventsCards() {
        let actEvents = this.props.events;
        let res = "";

        //console.log(this.props.categoryInfo);

        res = actEvents.map(e => (
            //console.log("CategoryPage | loadEventsCards | Event: " + e),
            //console.log(e),
            <EventCard key={e.Name + "Card"} eventInfo={e} />
        ));

        return res;
    }

    render() {
        return (
            <div id={this.props.categoryInfo.name} className="container category">

                {this.props.userData.type == "ADMIN" ?
                    <div id="Admin Bar" className="row verticalCenteredParent">

                        <div className="col">
                            <h1>{this.props.categoryInfo.name}</h1>
                        </div>

                        <div className="col pullRight">
                            <button type="button" className="btn btn-danger pull-right" data-toggle="modal" data-target="#AddEventModal">
                                Add {this.props.categoryInfo.name} Event
                            </button>
                        </div>


                        <div className="modal fade" id="AddEventModal" tabIndex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                            <div className="modal-dialog" role="document">
                                <div className="modal-content">
                                    <div className="modal-header">
                                        <h5 className="modal-title" id="exampleModalLabel">Add Event</h5>
                                        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                            <span aria-hidden="true">&times;</span>
                                        </button>
                                    </div>

                                    <div className="modal-body">
                                        <div className="form-group">
                                            <div className="input-group mb-2">
                                                <div className="input-group-prepend">
                                                    <div className="input-group-text">Name: </div>
                                                </div>
                                                <input type="text" className="form-control" id="inlineFormInputGroup" placeholder="Event name..." />
                                            </div>
                                        </div>


                                        <div className="form-group">
                                            <div className="input-group mb-2">
                                                <div className="input-group-prepend">
                                                    <div className="input-group-text">Place: </div>
                                                </div>
                                                <input type="text" className="form-control" id="inlineFormInputGroup" placeholder="Event place..." />
                                            </div>
                                        </div>

                                        <div class="form-row">
                                            <div class="form-group col-md-6">
                                                <label for="inputEmail4">Team 1</label>
                                                <input type="text" class="form-control" id="inputEmail4" placeholder="Team 1 Name..." />
                                            </div>
                                            <div class="form-group col-md-6">
                                                <label for="inputPassword4">Team 2</label>
                                                <input type="text" class="form-control" id="inputPassword4" placeholder="Team 2 Name..." />
                                            </div>
                                        </div>

                                        <div class="form-row">
                                            <div className="input-group col-md-6">
                                                <div className="input-group-prepend">
                                                    <div className="input-group-text">Probability: </div>
                                                </div>
                                                <input type="text" className="form-control" id="inlineFormInputGroup" placeholder="Team 1 probability..." />
                                            </div>
                                            
                                            <div className="input-group col-md-6">
                                                <div className="input-group-prepend">
                                                    <div className="input-group-text">Probability: </div>
                                                </div>
                                                <input type="text" className="form-control" id="inlineFormInputGroup" placeholder="Team 2 probability..." />
                                            </div>
                                        </div>
                                        
                                        <div className="form-group">
                                            <div className="input-group mb-2">
                                                <div className="input-group-prepend">
                                                    <div className="input-group-text">Tie probability: </div>
                                                </div>
                                                <input type="text" className="form-control" id="inlineFormInputGroup" placeholder="Tie probability..." />
                                            </div>
                                        </div>

                                        <div className="modal-footer">
                                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Cancel</button>
                                            <button onClick={() => this.AddEvent()} type="button" className="btn btn-primary">Save event</button>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>

                    : <h1>{this.props.categoryInfo.name}</h1>}

                <hr className="my-4" />

                <div className="row">
                    {this.loadEventsCards()}
                </div>
            </div>
        );
    }
}

CategoryPage.propTypes = {
    categoryInfo: PropTypes.object.isRequired,
    events: PropTypes.array.isRequired,
    userData: PropTypes.object.isRequired
}

export default CategoryPage;
