import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withTracker } from "meteor/react-meteor-data";

import EventBetCard from "./EventBetCard.jsx";
import { Events } from "../../api/Events";
import "../css/MyBets.css";

class MyBets extends Component {

    componentDidMount() {
        //console.log(this.props.myBets);
    }

    AddBet(eventId, prob1, prob2, probT, bet1, bet2, betT, earnings) {
        alert("MyBets | Adding bet : " + bet1 + "|" + betT + "|" + bet2 + " | Earnings: " + earnings);

        Meteor.call("UserData.addBet", eventId, prob1, prob2, probT, bet1, bet2, betT, earnings, (err, res) => {
            if (err) {
                alert(err);
            } else {
                alert("Bet created!");
            }
        });
    }

    loadBetsList() {
        let actBets = this.props.myBets;

        if (actBets && actBets.length > 0) {
            let res = []

            //console.log(this.props.categoryInfo);

            /*res = actBets.map(e => (
                //console.log("MyBets | loadBetsList | Bet: " + e),
                //console.log(e),
                <EventBetCard key={e.Name + "BetCard"} eventBetsInfo={e} eventInfo={Events.find({ _id: e.EventId }, { limit: 1 }).fetch()} AddBet={(b1, b2, bT, eR) => this.AddBet(b1, b2, bT, eR)} />
            ));*/

            res = []

            console.log(actBets);

            let lastEventId = actBets[0].eventId;
            let sup = [];
            let i = 0;

            for (i = 0; i < actBets.length; i++) {
                let e = actBets[i];

                if (lastEventId !== e.eventId) {
                    lastEventId = e.eventId;
                    res.push(
                        <EventBetCard key={"BetCard#" + (i + 1)} eventBetsInfo={sup} eventInfo={Events.find({ _id: e.eventId }, { limit: 1 }).fetch()} AddBet={(b1, b2, bT, eR) => this.AddBet(b1, b2, bT, eR)} />
                    );

                    sup = [];
                } else {
                    sup.push(e);
                }

                if (i == actBets.length - 1) {
                    res.push(
                        <EventBetCard key={"BetCard#" + (i + 1)} eventBetsInfo={sup} eventInfo={Events.find({ _id: e.eventId }, { limit: 1 }).fetch()} AddBet={(b1, b2, bT, eR) => this.AddBet(b1, b2, bT, eR)} />
                    );
                }
            }
            return res;
        }

    }

    render() {
        return (
            <div id="MyBets" className="container myEvents">
                <h1>My Bets</h1>

                <hr className="my-4" />

                <div className="row">
                    {this.loadBetsList()}
                </div>
            </div>
        );
    }
}

MyBets.propTypes = {
    myBets: PropTypes.array.isRequired
}


export default withTracker(
    () => {
        Meteor.subscribe("Events");

        return {
            events: Events.find({}).fetch()
        }
    }
)(MyBets);
